import { Component, OnInit } from '@angular/core';
import { CartService } from '../cart.service';
import { car// Import your CartItem model/interface

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {
  cartItems: CartItem[] = [];
  totalPrice: number = 0;

  constructor(private cartService: CartService) {}

  ngOnInit(): void {
    this.cartItems = this.cartService.getCartItems();
    this.calculateTotalPrice();
  }

  calculateTotalPrice(): void {
    this.totalPrice = this.cartItems.reduce((total, item) => total + item.total, 0);
  }

  placeOrder(): void {
    // Implement the logic for placing the order here.
    // You can make an API request to a backend server to process the order.
    // This is a placeholder for demonstration purposes.
    alert('Order placed successfully!');
    this.cartService.clearCart(); // Clear the cart after placing the order.
    this.cartItems = []; // Clear the cart items in the component.
    this.totalPrice = 0; // Reset the total price.
  }
}
