import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { BookService } from '../services/book.service'; // Import your book service

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css'],
})
export class CheckoutComponent implements OnInit {
  bookId: number;
  book: any; // Define the book object

  constructor(
    private route: ActivatedRoute,
    private bookService: BookService // Inject your book service
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.bookId = +params['id']; // Get the book ID from the route
      // Fetch book details by ID using your book service
      this.bookService.getBookById(this.bookId).subscribe((data) => {
        this.book = data; // Assign the fetched book data to the 'book' object
      });
    });
  }
}
