import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  loading: boolean = true;

  ngOnInit() {
    // Simulating data loading delay
    setTimeout(() => {
      this.loading = false;
    }, 3000);
  }

}
<ul class="navbar">
  <li><a class="active" [routerLink]="['/home']">Home</a></li>
  <li><a class="active" [routerLink]="['/book-list']">BookList</a></li>
  <li><a [routerLink]="['/search']">Search</a></li>
  <li><a [routerLink]="['/book-availability']">Availability</a></li>
  <li><a [routerLink]="['/cart']">Cart</a></li>
  <li><a class="login" [routerLink]="['/login']">Login</a></li>
  <li><a [routerLink]="['/register']">Signup</a></li>
</ul>

<div class="loading-container" *ngIf="loading">
  <div class="loading-spinner"></div>
</div>

<h1 class="head">Welcome to e-lib</h1>
<p class="para">Explore the adventure of reading books</p>
<img class="image" src="https://media.istockphoto.com/id/955664362/vector/read-book.jpg?s=612x612&w=0&k=20&c=6TAHbBu2V0LzOZYzj6XGEYuKaHBQ4K_Uz69dleuMIFM=">
<br><br>
